import {Component, Injectable} from '@angular/core';
import {AbstractControl, FormControl, FormGroup, Validators} from "@angular/forms";
import {Observable} from "rxjs";
import {HttpClient} from "@angular/common/http";
import {httpRequests} from "./http";
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent{
  constructor(private httpRequest:httpRequests) {
  }

  group=new FormGroup({
    email:new FormControl(null,[],this.restricted),
    password:new FormControl(null,[Validators.required])
  });
  restricted(control:AbstractControl):Promise<any>|Observable<any>{
    let emails:any;
    this.httpRequest.getRequest().subscribe((data)=>{
      console.log(data);
    })

    let promise = new Promise((resolve, reject) => {
      setTimeout(() => {
        let email = control.value;
        if (email === null) {
          resolve({EmailError:true});
        } else {
          resolve({EmailError:false});
        }
      }, 2000)
    });
    return promise;
  }
  submit(){
    console.log(this.group)
  }
}
<!doctype html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport"
        content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">
  <meta http-equiv="X-UA-Compatible" content="ie=edge">
  <title>Login</title>
  <script src="https://code.jquery.com/jquery-3.7.0.js" integrity="sha256-JlqSTELeR4TLqP0OG9dxM7yDPqX1ox/HfgiSLBj8+kM=" crossorigin="anonymous"></script>
</head>
<body>
<div class="row">
  <div class="mx-auto col-10 col-md-8 col-lg-6">
    <form [formGroup]="group" (ngSubmit)="submit()">
      <div class="form-group">
        <label for="exampleInputEmail1">Email address</label>
        <input formControlName="email" name="email" type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email">
        <p style="color: red"></p>
      </div>
      <div class="form-group">
        <label for="exampleInputPassword1">Password</label>
        <input formControlName="password"  name="password" type="password" class="form-control" id="exampleInputPassword1" placeholder="Password">
        <p style="color: red"></p>
      </div><br>
      <button type="submit" class="btn btn-primary mx-2" [disabled]="group.invalid">Login</button>
      <button type="button" class="btn btn-primary">Sign up</button>
    </form>
  </div>
</div>
</body>
</html>
