import {Component, OnInit} from '@angular/core';
import {AbstractControl, FormControl, FormGroup, Validators} from "@angular/forms";
import {Observable} from "rxjs";
import {httpRequests} from "./http";
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent{
  constructor(private Request:httpRequests) {
  }
  group=new FormGroup({
    email:new FormControl(null,[Validators.required,Validators.email],this.restricted.bind(this)),
    password:new FormControl(null,[Validators.required,Validators.minLength(8)])
  });
  UserDoesNotExist(UserData:any,UserInput:any):Boolean{
      let UserExists:Boolean=false;
      let i=0;
      while(i<UserData.length && UserExists){
        if(UserInput==UserData[i].email){
          UserExists=true;
        }else{
          i++;
        }
      }

      return UserExists;
  }
  restricted(control:AbstractControl):Promise<any>|Observable<any>{
      let UserData:any;
      this.Request.GetAllUsers().subscribe((data)=>{
         UserData=data;

      })
      let promise = new Promise((resolve, reject) => {

      setTimeout(() => {
          let UserInput = control.value;
          if(this.UserDoesNotExist(UserData,UserInput)==false){
            resolve({error:"email does not exist"})
          }



      }, 1000)
    });
    return promise;
  }
  submit(){
    console.log(this.group)
  }
}
