import {Component, OnInit} from '@angular/core';
import {AbstractControl, FormControl, FormGroup, Validators} from "@angular/forms";
import {Observable} from "rxjs";
import {httpRequests} from "./http";
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent implements OnInit{

  ngOnInit() {
    this.Request.getRequest().subscribe((data)=>{
      this.emails=data;
    })
  }

  constructor(private Request:httpRequests,private emails:any) {
  }
  group=new FormGroup({
    email:new FormControl(null,[],this.restricted.bind(this)),
    password:new FormControl(null,[Validators.required])
  });
  restricted(control:AbstractControl):Promise<any>|Observable<any>{
      let promise = new Promise((resolve, reject) => {

      setTimeout(() => {
        let email = control.value;
        for(let i=0;i<this.emails.length;i++){
          console.log(this.emails[i]);
        }

      }, 2000)
    });
    return promise;
  }
  submit(){
    console.log(this.group)
  }
}
