import { Component } from '@angular/core';
import {AbstractControl, FormControl, FormGroup, Validators} from "@angular/forms";
import {Observable} from "rxjs";
import {resolve} from "@angular/compiler-cli";
import {httpRequests} from "../http";

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {
  results:any
  constructor(private Request:httpRequests) {
  }
  group=new FormGroup({
    name:new FormControl(null,[Validators.required]),
    email:new FormControl(null,[Validators.required,Validators.email]),
    password:new FormControl(null,Validators.required),
    passwordconfirm:new FormControl(null,[Validators.required],this.CheckPassword.bind(this)),
    CheckBox:new FormControl(null)
  },[],this.Validation.bind(this));
  CheckPassword(control:AbstractControl):Promise<any>|Observable<any>{
    let promise=new Promise((resolve,reject)=>{
      let password=this.group.controls.password.value;
      if(password!=control.value){
        resolve({PassError:"invalid"});
      }else{
        resolve(null);
      }

    })
    return promise;
  }
  CheckEmail(result:any,email:any):boolean{
    let ok:boolean=true;
    result.forEach(function (element:any){
      if(element.email==email){
        ok=false;
      }
    })
    return ok;
  }
  Validation(control:AbstractControl):Promise<any>|Observable<any>{
    this.Request.GetAllUsers().subscribe((data)=>{
       this.results=data;
    })
    console.log(this.results);
    let promise=new Promise((resolve,reject)=>{
        console.log(this.group.controls.email.value);
        if(!this.CheckEmail(this.results,control.value.email)){
          resolve({EmailError:"true"})
        }
        else{
          resolve(null);
        }

    })
    return promise;
  }
  submit(){
    this.group;

  }
}
