import { Component } from '@angular/core';
import {AbstractControl, FormControl, FormGroup, Validators} from "@angular/forms";
import {Observable} from "rxjs";

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {
  group=new FormGroup({
    name:new FormControl(null,[Validators.required]),
    email:new FormControl(null,[Validators.required,Validators.email]),
    password:new FormControl(null,Validators.required),
    passwordconfirm:new FormControl(null,[Validators.required],this.CheckPassword.bind(this)),
    CheckBox:new FormControl(null,[Validators.required])
  })
  CheckPassword(control:AbstractControl):Promise<any>|Observable<any>{
    let promise=new Promise((resolve,reject)=>{
      let password=this.group.controls.password.value;
      if(password!=control.value){
        resolve({PassError:"invalid"});
      }else{
        resolve(null);
      }

    })
    return promise;
  }
  submit(){
    console.log(this.group);
  }
}
